// Util functions for operations usually used with files.

'use strict';

/**
 * Split the given source string into its lines.  Respects Unix,
 * Windows und Macintosh line breaks.
 *
 * @param {string} src
 * @returns {string[]}
 */
function splitLines(src) {
  return src.split(/\r\n?|\n/);
}

module.exports = {
  splitLines
}
