/* eslint-disable no-console */
class ClientLog {

    static log(logText) {
        console.log(logText);
    }

    static error(errorText, error) {
        console.error(this.getMessage(errorText, error));
    }

    static debug(debugText, error) {
        if (process.env.DEBUG) {
            console.error(this.getMessage(debugText, error));
        }
    }

    static getMessage(errorText, error) {
        let message = errorText || '';
        if (message && error) {
            message += '\n';
        }
        if (error) {
            message += process.env.DEBUG ? '\n' + error.stack : error.message;
        }
        return message;
    }
}

module.exports = ClientLog;
