const { Query } = require('./Whereable')
const $ = Object.assign

module.exports = class CREATE extends Query {
  static get _() {
    return $((..._) => new this().entity(..._), {
      entity: (..._) => new this().entity(..._),
    })
  }
  entity(e, elements) {
    this._.entity = e
    this._.elements = elements
    return this
  }
  as(query) {
    this._.as = query
    return this
  }
}
